AS
BEGIN
	DECLARE @CSISUM INT = 0;
	DECLARE @Result INT = 1;
	SELECT  @CSISUM = ISNULL(SUM(CAST(CSI AS INT)), 0) FROM ToolStringComponentInfo(NOLOCK) WHERE RunID = @RunId AND FixedAssetID = @FixedAssetId
	
	IF(@CSISUM >= 1)
	BEGIN SET @Result = 3 END
	ELSE
