GROUP BY pft.ItemNum, i.ItemNumId, i.DescShort, pft.BranchPlant, pftc.ProcessName, pftc.ProcessDesc, pft.PFTConfigId, (pftcs.SeqName + ' (' + ltrim(str(pftcs.Seq)) + ')'), pftcs.Seq, CurPFTWOSeqId--, ibp.StdUnitCost
