CREATE VIEW [dbo].[vwJobSearch] AS
SELECT j.JobID
	, j.Region
	, j.BranchPlant As District
	, j.Country
	, j.CustomerId
	, cu.CustomerNumber
	, j.JobNumber
	, reg.RegionDesc
	, d.RelBaseCode + ' - ' + d.BranchPlant + ' - ' + d.CompanyName As DistrictDesc
	, c.CountryDesc
	, j.LocationState
	, j.LocationCounty
	, cu.CustomerName
