				JOIN		ToolStringComponentInfo tc ON tc.ToolStringID = t1.ToolStringId
				GROUP BY	t1.ToolStringId,RunID
				) a
				ON a.RunID = r.RunID and a.ToolStringId = @toolStringId
	JOIN		ToolStrings t ON t.ToolStringId = a.ToolStringId

