CREATE VIEW [dbo].[vwJobDTIntegration]
AS
SELECT * 
FROM 
(	SELECT 
		ROW_NUMBER() OVER(PARTITION BY f.FixedAssetId ORDER BY f.FixedAssetId asc, d.DispatchNumber desc) [Index], 
		f.FixedAssetId
		,f.SerialNum
		,f.InventoryItemNum
		,f.EquipmentStatus
		,ps.[Status] AS StatusDesc
		,f.BranchPlant
		,bp.CompanyName AS BranchPlantDesc
		,f.Revision
		,d.DispatchNumber
		,f.LastComments
