CREATE VIEW [dbo].[vwJobImportStatusReport]
AS

SELECT j.JobId
, IsNull(dbo.fnJobImportStatus(j.JobId),'') As ImportStatus
, IsNull(j.ConfirmedClosed,0) As ConfirmedClosed
, IsNull(dbo.fnJarRevStatus(j.JobId),'') As JarStatus
