CREATE VIEW [dbo].[vwGFAIncidents]
AS
SELECT 
	gfa.GFAId,
	gfa.GFANumber,
	r.Region,
	r.RegionDesc,
	cntry.Country,
	cntry.CountryDesc,
	b.BranchPlant,
	j.JobId,
	j.JobNumber,
	j.Field,
	j.Rig,
	j.JarWell,
	(CASE WHEN ISNULL(gfa.WellName, '') = '' THEN j.JarWell ELSE gfa.WellName END) as WellName,
	j.StartDate,
	j.EndDate,
	cmp.CustomerName,
	gfa.RunNumber,
	gfa.FailureDate,
	gfa.TFF,
	gfa.CI,
	gfa.FailureDesc,
	gfa.PossibleCauses,
	gfa.ProductLine,
	pl.GFAServiceLine as ProductLineDisplay,
	gfa.EmployeeId,
	gfa.CreateDate,
	DATEDIFF(d, gfa.FailureDate, gfa.CreateDate) AS DaysToReport,
	gfa.Disposition,
	gfa.NPT,
	gfa.Summary,
	gfa.Is3030,
	gfa.AcceptedFieldFailure,
	
