	from GFAComponents ci WITH(NOLOCK)
	JOIN PartTypes pt ON pt.PartTypeId = ci.PartTypeId
	WHERE ci.GFAId = gfa.GFAId
	FOR XML PATH('')), 1, 1, '') AS ComponentNames,

