CREATE VIEW [dbo].[vwToolSheet] 
AS
 SELECT NEWID() Id
, t.ToolStringID
, t.SerialNumber as ToolString
, t.[Description] as ToolStringDesc
, CONVERT(VARCHAR, t.CreateDate, 107) As CreateDate
, t.CreatedBy
, t.CreateUserID
, CASE UPPER(ISNULL(t.[Type],'')) 
