	fa.BranchPlant,
	bp.CompanyName as BranchPlantName,
	(SELECT TOP 1 JDEWorkOrderNum from WorkOrders (NOLOCK) w WHERE w.FixedAssetId = fa.FixedAssetId
		ORDER BY DateAdded DESC) AS JDEWorkOrderNum,
