CREATE VIEW [dbo].[vwRequestItemGroupDetails]
AS
SELECT

  ROW_NUMBER() OVER (ORDER BY (i.ItemNum)) AS [RequestItemGroupId],
  rig.ParentItemNum AS GroupItemNum,
  i.ItemNum,
  i.ItemNum2,
  i.ItemNum3,
  i.LegacyNum,
  i.ToolPanel,
  i.ToolCode,
  i.DescShort,
  i.DescLong,
