  ISNULL(i.Critical, 0) AS Critical,
  i.IsObsolete,
  i.IsAsset,
  i.DefaultSerialProfile,
  i.ProductLine,
  CASE i.IsAsset
    WHEN 1 THEN 'Y'
    ELSE 'N'
  END AS IsAssetDisplay,
  i.StdUnitCost,
  'USD' AS CurrShortName,
  NULL AS DateLastEdit,
  0 AS UserIdLastEdit,
  rig.DateAdded,
  rig.UserIdAdded,
  0 AS DisplayOrder,
  NULL AS Name,
  NULL AS GroupToolPanel,
  NULL AS Description,
