--SELECT f1.FixedAssetId, pc.PFTConfigId, m.MeterReadingTypeId, 1 [TriggerValue], null [MeterReadingValue], GETDATE() [CreatedOn], m.ObjectNumber ,NULL [PFTWOId],m.CBMMonitorId
