            SELECT @dispatchInstanceId = DispatchInstanceId
            FROM DispatchInstances (NOLOCK) 
            WHERE DispatchId = @dispatchId
                AND SequenceNum = @sequenceNum
                AND ReturnSequenceNum = @returnSequenceNum
                AND ShipType = 'DT-RETURN'
        
