                    UPDATE DispatchInstanceItems 
                    SET QtyReturned = QtyReturned - @qtyReturned
                        , DateReturned = NULL
                        --, ReasonCode = NULL
                    where DispatchInstanceItemId = @dispatchInstanceItemId              

                    DELETE FROM DispatchInstanceItems 
                    WHERE DispatchInstanceId = @dispatchInstanceId
                        AND QtyReturned <= 0
                end
            end
        end
    END
    
