        , @CatCode16 = ltrim(rtrim(T.c.value('(Cat_Code_16/text())[1]', 'varchar(3)')))
        , @Revision = ltrim(rtrim(T.c.value('(Revision/text())[1]', 'varchar(2)')))
        --, @CatCode20
        , @ParentAsset = ltrim(rtrim(T.c.value('(ParentAsset/text())[1]', 'varchar(10)')))
