                 , ParentNumber = @ParentAsset
                 --, DateDisposed = @DateDisposed
                 , ModelYear = @ModelYear
                 , LastStatusChangeDate = @DateofTransaction
            WHERE FixedAssetId = @FixedAssetId
        end
