        , @ContractAccount varchar(25)
        , @Ownership varchar(3)
        , @DateAcquired datetime
        , @LifeMonths numeric(4)
        , @StartDepreciation_Date datetime
        , @NewUsed char(1)
        , @Manufacturer varchar(3)
        , @PanelCodeVersion varchar(25)
    --  , @AssetAdditionDate
