	@sToolStringInfoID as uniqueidentifier,
	@sIncidentID as uniqueidentifier,
	@sToolStringID as uniqueidentifier,
	@sFixedAssetID as uniqueidentifier,
	@sFailureCodeID as uniqueidentifier,
	@iUserID as int) 
