CREATE PROCEDURE [dbo].[usp_CPAR_GetIncidentIdsForJob]  
(  
	@jobNumber  VARCHAR(50)  
)  
AS  
BEGIN  
  
 SET NOCOUNT ON  
   
 BEGIN  
    
  SELECT DISTINCT  
    CONVERT(NVARCHAR(50),I.IncidentID) As IncidentID
   FROM  Incidents I   
   INNER JOIN Runs R on I.RunID = R.RunID  
   INNER JOIN Wells W on W.WellID = R.WellID  
