BEGIN
	Set @StartDate = CONVERT(DATE, DATEADD(month, DATEDIFF(month, 0, @StartDate), 0), 101)  -- first day of month
	Set @EndDate = CONVERT(DATE, DATEADD(s, -1, DATEADD(mm, DATEDIFF(m, 0, @EndDate) + 1, 0)), 101)  -- last day of month
