SET @pagingSql = 'SELECT TOP ' + @pageSize + ' page.* FROM (
		SELECT ROW_NUMBER() OVER(ORDER BY ' + @sortField + ' ' + @sortDir + ') as RowNum, * From ( ' + 
		@sql + ') query ) page 
		where page.RowNum > ' + @startIndex + ' order by ' + @sortField + ' ' + @sortDir
		


EXECUTE(@pagingSql)

