	@WindchillDocNumber VARCHAR(100),
	@PFTType INT,
	@ProductlineId INT,
	@CreatedBy VARCHAR(20),
	@PFTStepObjectNumbers VARCHAR(MAX)
AS

DECLARE @PftConfigObjectId int;

BEGIN TRY
		
	BEGIN TRANSACTION

	INSERT INTO [dbo].[PFTObjects]
				([ObjectNumber]
				,[Revision]
				,[Type]
				,[Name]
				,[Description]
				,[ProductLineId]
				,[State]
				,[Field1]
				,[Field2]
				,[CreatedBy]
				,[CreatedOn])
			SELECT
				MAX(ObjectNumber) + 1
				,1
				,1
				,@PFTConfigName
				,@PFTConfigName
				,@ProductlineId
				,1
				,@PFTType
				,@WindchillDocNumber
				,@CreatedBy
				,GETDATE()
		FROM PFTObjects

	SET @PftConfigObjectId = @@IDENTITY

	INSERT INTO [dbo].[PFTObjectMappings]
				([ParentId]
				,[ObjectId]
				,[SequenceNumber]
				,[CreatedBy]
				,[CreatedOn])
			SELECT
				@PftConfigObjectId,
				o.ObjectId,
				s.NodeLevel,
				@CreatedBy,
				GETDATE()
		FROM dbo.Split(',', @PFTStepObjectNumbers) s
		JOIN PFTObjects o ON ltrim(rtrim(s.val)) = o.ObjectNumber and o.[State] = 3
		ORDER BY s.NodeLevel ASC

	COMMIT TRANSACTION

