CREATE PROC [dbo].[usp_RemoveChildAsset]
	@FixedAssetId UNIQUEIDENTIFIER,
	@UserId VARCHAR(10)
AS
BEGIN
	BEGIN TRY
		BEGIN TRAN
			DECLARE	@ParentFixedAssetId UNIQUEIDENTIFIER;
			DECLARE @AuditActionId INT;
			DECLARE @ParentAssetNum VARCHAR(50);
			DECLARE @ParentSerialNum VARCHAR(50);
			DECLARE @ParentItemNum VARCHAR(50);

			SELECT	@ParentFixedAssetId = [ParentFixedAssetId] FROM [FixedAssets](NOLOCK) WHERE [FixedAssetId] = @FixedAssetId

			IF(@ParentFixedAssetId IS NOT NULL)
			BEGIN

				--Create Part Transfer Record
				INSERT INTO PartTransferDtl (
						PartTransferDtlId,
						JDETransactionId,
						FixedAssetId,
						AssetNumber,
						DocumentType,
						DocumentNumber,
						AddressNumber,
						InventoryItemNum,
						RNItemNum,
						SerialNum,
						Quantity,
						AssetStatus,
						DateShipConfirmed,
						UserCreatedDocument,
						UserApprovedonRecvSide,
						DateApproved,
						SendingLocation,
						ReceivingLocation,
						AssetTransferReqNo,
						DateofTransaction,
						OwnershipCode,
						Source,
						Dest,
						DateAdded,
						ParentNumber)
					SELECT 
						NEWID(),
						NULL,
						@fixedAssetId,
						AssetNumber,
						'MAPC',
						NULL,
						NULL,
						InventoryItemNum,
						RNItemNum,
						SerialNum,
						0,
						EquipmentStatus,
						NULL,
						@UserId,
						NULL,
						NULL,
						BranchPlant,
						0,
						NULL,
						GETDATE(),
						NULL,
						EquipmentStatus,
						'AV',
						GETDATE(),
						ParentNumber
					FROM FixedAssets
					WHERE FixedAssetId = @FixedAssetId

				UPDATE  [dbo].[FixedAssets]
				SET		[ParentNumber] = AssetNumber,
						[ParentFixedAssetId] = NULL,
						[EquipmentStatus] = 'AV'
				WHERE	[FixedAssetId] = @FixedAssetId


				-- Reset Top Level FixedAssetId 
				exec usp_UpdateTopLevelFixedAssetId @FixedAssetId

			END
		COMMIT TRAN
	END TRY
	BEGIN CATCH
		IF @@TRANCOUNT > 0
		ROLLBACK TRANSACTION;
		DECLARE @ErrorMessage VARCHAR(4000) = ERROR_MESSAGE();
		DECLARE @ErrorSeverity INT = ERROR_SEVERITY();
		DECLARE @ErrorState INT = ERROR_STATE();
		RAISERROR (@ErrorMessage, @ErrorSeverity, @ErrorState);
	END CATCH
END
