	OptionValue VARCHAR(50),
    OptionLabel VARCHAR(50)
)

IF EXISTS(SELECT T.c.value('.', 'varchar(100)') AS StatusId FROM @xmlData.nodes('/Filters/ToolPanels') T(c))
