	end
	
	if ((Select COUNT(*) from #CustomProperties) != 0)
	begin
		set @sWhere = @sWhere + ' And ToolStringComponentInfo.CustomProperty in (select CustomProperty from #CustomProperties)'
	end

	if (@tffCiNci <> '')
