SELECT @startIndex = T.c.value('.', 'VARCHAR(10)') FROM @xmlData.nodes('/Filters/StartIndex')T(c);
SELECT @pageSize = T.c.value('.', 'VARCHAR(10)') FROM @xmlData.nodes('/Filters/PageSize')T(c);
