LEFT JOIN Hydraulics h WITH(NOLOCK) ON h.RunID = run.RunID
LEFT JOIN Incidents i WITH(NOLOCK) ON i.RunID = run.RunID
LEFT JOIN vwIncidentFaultCodes ifc WITH(NOLOCK) ON ifc.IncidentId = I.IncidentID
LEFT JOIN
