BEGIN

SET NOCOUNT ON;

DECLARE @WhereClause AS VARCHAR(MAX)

SELECT @WhereClause = T.c.value('.', 'VARCHAR(max)') FROM @xmlData.nodes('/Filters/WhereClause')T(c);


DECLARE @startIndex AS VARCHAR(10)
