INSERT INTO #tblData
SELECT  jrft.JarRunForecastToolId, ic.ItemNumConfigId, j.JobId, j.JobNumber, i.DescShort,i.ItemNum, SUM(ISNULL(ic.Qty,0) * ISNULL(jrft.Qty,0))
