CREATE PROCEDURE [dbo].[usp_UpdateJobStatus] (
	@sJobID as uniqueidentifier,
	@sStatus as varchar(15)) 

AS
BEGIN
SET NOCOUNT ON;

DECLARE @sOrigStatus varchar(15)
SET @sOrigStatus = (Select ImportStatus From Jobs (NOLOCK) Where JobID = @sJobID)

