	WHERE JobID = @sJobID

	IF (@sStatus = 'Active')
	BEGIN
		Update Jobs
		Set DateActive = GetDate()
		Where JobID = @sJobID
	END
	ELSE IF (@sStatus = 'Pending')
	BEGIN
		Update Jobs
		Set DatePending = GetDate()
		Where JobID = @sJobID
	END
	ELSE IF (@sStatus = 'Projected')
	BEGIN
		Update Jobs
		Set DateProjected = GetDate()
		Where JobID = @sJobID
	END
	ELSE IF (@sStatus = 'Closed')
	BEGIN
		Update Jobs
		Set DateClosed = GetDate()
		Where JobID = @sJobID
	END
END

SET NOCOUNT OFF;
END




