		SELECT -1;
		RETURN ;
	END 

	IF(@ParentFixedAssetId IS NULL)
	BEGIN 
		SELECT -1;
		RETURN ;
	END 

	IF(@ChildFixedAssetId = @ParentFixedAssetId)
	BEGIN 
		SELECT -1;
		RETURN ;
