END	

IF((select count(*) from #ToolPanel) > 0)
BEGIN
		SET @whereClause = @whereClause + ' AND ToolPanel in (select ToolPanel from #ToolPanel)'
END		

IF((select count(*) from #IsGlobalRepair) > 0)
BEGIN
