END						

IF(@StartDate IS NOT NULL AND @EndDate IS NOT NULL )
	BEGIN
			SET @whereClause = @whereClause + ' AND  ' + 'CONVERT(datetime, CONVERT(VARCHAR(4), [year]) + ''-'' + CONVERT(VARCHAR(4),[Month]) + ''-01'', 101)'
			+ ' BETWEEN ''' + @StartDate + ''' AND ''' + @EndDate +''''
