    			INNER JOIN vwPFTWOSeq pftwos on pfwo.PFTWOId=pftwos.PFTWOId
				LEFT OUTER JOIN PFTConfig WITH(NOLOCK) ON pfwo.PFTConfigId = PFTConfig.PFTConfigId 
